import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  playInstrument(e) {
    var prettySound = new Audio(e.target.dataset.sound);
    prettySound.play();
  }

  changeImage(img) {
    var selectedPhoto = document.getElementById(img).src;
  }

  render() {
    return (
        <section class="container">
          <div className="left-half">
            <img src="pics/TimelinePhotos/AnaandJared_Christmas2008.jpg" alt="Timeline" width="1000" />
          </div>

          <div className="right-half" onClick={this.playInstrument} onClick={this.changeImage}>
            <p>2002</p>
            <img src='pics/TimelinePhotos/AnaandJared_Christmas2008.jpg' id="Ana" className="input" width="400" />
            <p>2003</p>
            <img src="pics/TimelinePhotos/AnaChristmasTimeCabelas.jpg" className="input" width="400" />
            <p>2004</p>
            <img src="pics/TimelinePhotos/AnaJaredandStacy_Christmas2008.jpg" className="input" width="400" />
            <p>2005</p>
            <img src="pics/TimelinePhotos/Aug2010FamilyReunionCoeurDLeneLake.jpg" data-sound="music/banjo.mp3" className="input" width="400" />
            <p>2006</p>
            <img src="pics/TimelinePhotos/BabyJaredwithAna.JPG" alt="cello" className="input" width="400" />
            <p>2007</p>
            <img src="pics/TimelinePhotos/BabyJaredwithAna.JPG" data-sound="music/clarinet.mp3" className="input" width="400" />
            <p>2008</p>
            <img src="pics/TimelinePhotos/TN_drum.jpg" data-sound="music/snare-drum.mp3" className="input" width="400" />
            <p>2009</p>
            <img src="pics/TimelinePhotos/TN_french_horn.jpg" className="input" />
            <p>2010</p>
            <img src="pics/TimelinePhotos/TN_harp.jpg" className="input" />
          </div>
        </section>
    );
  }
}

export default App;
